import React from 'react';
import Pagination from '../src';

export default function Maxlinks(args) {
  // eslint-disable-next-line no-console
  const onChange = (pageSelected, pageItems) => console.log(`Page selected: ${pageSelected}, Page Items: ${pageItems}`);

  return (
    <div>
      <h3>Default max links</h3>
      <Pagination
        onChange={ onChange }
        { ...args }
      />
      <hr/>
      <h3>Override for max links with value &apos;3&apos;</h3>
      <Pagination
        onChange={ onChange }
        maxPageLinks={ 3 }
        { ...args }
      />
    </div>
  );
}

Maxlinks.args = {
  totalItems: 100,
  pageItems: 20
};
