import React from 'react';
import Pagination from '../src';

export default function NoItems(args) {
  return (
    <div>
      <Pagination
        onChange={ e => e }
        { ...args }>
        No items to display.
      </Pagination>
    </div>
  );
}

NoItems.args = {
  defaultSelected: 10,
  pageItems: 0,
  totalItems: 0
};
