import React from 'react';
import Pagination from '../src';

export default function Sizes(args) {
  // eslint-disable-next-line no-console
  const onChange = (pageSelected, pageItems) => console.log(`Page selected: ${pageSelected}, Page Items: ${pageItems}`);

  return (
    <div>
      <h3>Small</h3>
      <Pagination
        defaultSelected={ 1 }
        onChange={ onChange }
        size='sm'
        id='small'
        { ...args }
      />
      <hr/>
      <h3>Medium</h3>
      <Pagination
        defaultSelected={ 1 }
        onChange={ onChange }
        size='md'
        id='medium'
        { ...args }
      />
      <hr />
      <h3>Large</h3>
      <Pagination
        onChange={ onChange }
        defaultSelected={ 1 }
        size='lg'
        id='large'
        { ...args }
      />
    </div>
  );
}

Sizes.args = {
  pageItems: 20,
  totalItems: 100,
  pageItemsChoices: [5, 10, 20]
};
