import Intents, { enums } from '@ux/intents';

// eslint-disable-next-line max-statements
export default (function () {
  const intents = new Intents();

  intents.create('@ux/pivot', {
    styleProperties: [
      enums.properties.BACKGROUND_COLOR,
      enums.properties.BORDER_COLOR,
      enums.properties.BORDER_RADIUS
    ],
    interactions: [
      enums.interactions.HOVERED,
      enums.interactions.FOCUSED
    ],
    expressions: [
      enums.expressions.CHOSEN
    ],
    features: {
      [enums.features.TITLE]: {
        styleProperties: [
          enums.properties.FONT_FAMILY,
          enums.properties.FONT_SIZE,
          enums.properties.FONT_WEIGHT,
          enums.properties.LINE_HEIGHT,
          enums.properties.FOREGROUND_COLOR
        ]
      },
      [enums.features.DESCRIPTION]: {
        styleProperties: [
          enums.properties.FONT_FAMILY,
          enums.properties.FONT_SIZE,
          enums.properties.FONT_WEIGHT,
          enums.properties.LINE_HEIGHT,
          enums.properties.FOREGROUND_COLOR
        ]
      },
      [enums.features.INDICATOR]: {
        styleProperties: [
          enums.properties.FOREGROUND_COLOR
        ]
      }
    }
  });

  // Each card component
  intents.uxPivot.backgroundColor = intents.ux.box.backgroundColor;
  intents.uxPivot.borderColor = intents.ux.box.borderColor;
  intents.uxPivot.borderRadius = intents.ux.box.borderRadius;

  // interactions/expressions card
  intents.uxPivotHovered.backgroundColor = intents.ux.box.backgroundColor;
  intents.uxPivotHovered.borderColor = intents.ux.boxLowContrast.borderColor;
  intents.uxPivotFocused.backgroundColor = intents.ux.box.backgroundColor;
  intents.uxPivotFocused.borderColor = intents.ux.boxLowContrast.borderColor;
  intents.uxPivotChosen.backgroundColor = intents.ux.box.backgroundColor;
  intents.uxPivotChosen.borderColor = intents.ux.boxLowContrast.borderColor;

  // Title
  intents.uxPivot.title.fontFamily = intents.ux.textTitle.fontFamily;
  intents.uxPivot.title.fontSize = intents.ux.textTitle.fontSize;
  intents.uxPivot.title.fontWeight = intents.ux.textTitle.fontWeight;
  intents.uxPivot.title.lineHeight = intents.ux.textTitle.lineHeight;
  intents.uxPivot.title.foregroundColor = intents.ux.box.foregroundColor;

  // Description
  intents.uxPivot.description.fontFamily = intents.ux.textParagraph.fontFamily;
  intents.uxPivot.description.fontSize = intents.ux.textParagraph.fontSize;
  intents.uxPivot.description.fontWeight = intents.ux.textParagraph.fontWeight;
  intents.uxPivot.description.lineHeight = intents.ux.textParagraph.lineHeight;
  intents.uxPivot.description.foregroundColor = intents.ux.box.foregroundColor;

  // Arrow
  intents.uxPivot.indicator.foregroundColor = intents.ux.feedback.feedbackColor;

  return intents;
}());
