import Intents, { enums } from '@ux/intents';

// eslint-disable-next-line max-statements
export default (function () {
  const intents = new Intents();

  intents.create('@ux/progress-bar', {
    styleProperties: [
      enums.properties.BACKGROUND_COLOR,
      enums.properties.BORDER_RADIUS
    ],
    features: {
      [enums.features.TITLE]: {
        styleProperties: [
          enums.properties.FONT_FAMILY,
          enums.properties.FONT_SIZE,
          enums.properties.FONT_WEIGHT,
          enums.properties.LINE_HEIGHT,
          enums.properties.FOREGROUND_COLOR
        ]
      },
      [enums.features.ITEM]: {
        styleProperties: [
          enums.properties.FONT_FAMILY,
          enums.properties.FONT_SIZE,
          enums.properties.FONT_WEIGHT,
          enums.properties.LINE_HEIGHT,
          enums.properties.FOREGROUND_COLOR,
          enums.properties.BACKGROUND_COLOR
        ]
      },
      [enums.features.METER]: {
        expressions: [
          enums.expressions.CRITICAL,
          enums.expressions.WARNING,
          enums.expressions.SUCCESS,
          enums.expressions.INFO
        ],
        styleProperties: [
          enums.properties.FONT_FAMILY,
          enums.properties.FONT_SIZE,
          enums.properties.FONT_WEIGHT,
          enums.properties.LINE_HEIGHT,
          enums.properties.FOREGROUND_COLOR,
          enums.properties.BACKGROUND_COLOR
        ]
      },
      [enums.features.DESCRIPTION]: {
        styleProperties: [
          enums.properties.FONT_FAMILY,
          enums.properties.FONT_SIZE,
          enums.properties.FONT_WEIGHT,
          enums.properties.LINE_HEIGHT,
          enums.properties.FOREGROUND_COLOR
        ]
      }
    }
  });

  // Empty bar
  intents.uxProgressBar.backgroundColor = intents.ux.boxLowContrast.backgroundColor;
  intents.uxProgressBar.borderRadius = intents.ux.box.borderRadius;

  // Title
  intents.uxProgressBar.title.fontFamily = intents.ux.textLabel.fontFamily;
  intents.uxProgressBar.title.fontSize = intents.ux.textLabel.fontSize;
  intents.uxProgressBar.title.fontWeight = intents.ux.textLabel.fontWeight;
  intents.uxProgressBar.title.lineHeight = intents.ux.textLabel.lineHeight;
  intents.uxProgressBar.title.foregroundColor = intents.ux.box.foregroundColor;

  // Tick marks and labels
  intents.uxProgressBar.item.fontFamily = intents.ux.textLabel.fontFamily;
  intents.uxProgressBar.item.fontSize = intents.ux.textLabel.fontSize;
  intents.uxProgressBar.item.fontWeight = intents.ux.textLabel.fontWeight;
  intents.uxProgressBar.item.lineHeight = intents.ux.textLabel.lineHeight;
  intents.uxProgressBar.item.foregroundColor = intents.ux.box.foregroundColor;
  intents.uxProgressBar.item.backgroundColor = intents.ux.box.backgroundColor;

  // Notice <small>
  intents.uxProgressBar.description.fontFamily = intents.ux.textCaption.fontFamily;
  intents.uxProgressBar.description.fontSize = intents.ux.textCaption.fontSize;
  intents.uxProgressBar.description.fontWeight = intents.ux.textCaption.fontWeight;
  intents.uxProgressBar.description.lineHeight = intents.ux.textCaption.lineHeight;
  intents.uxProgressBar.description.foregroundColor = intents.ux.box.foregroundColor;

  // Text inside the bar
  intents.uxProgressBar.meter.fontFamily = intents.ux.textCaption.fontFamily;
  intents.uxProgressBar.meter.fontSize = intents.ux.textCaption.fontSize;
  intents.uxProgressBar.meter.fontWeight = intents.ux.textCaption.fontWeight;
  intents.uxProgressBar.meter.lineHeight = intents.ux.textCaption.lineHeight;
  intents.uxProgressBar.meter.foregroundColor = intents.ux.feedback.onFeedbackColor;
  intents.uxProgressBar.meter.backgroundColor = intents.ux.feedback.feedbackColor;

  intents.uxProgressBar.meterCritical.foregroundColor = intents.ux.feedbackCritical.onFeedbackColor;
  intents.uxProgressBar.meterCritical.backgroundColor = intents.ux.feedbackCritical.feedbackColor;

  intents.uxProgressBar.meterWarning.foregroundColor = intents.ux.feedbackWarning.onFeedbackColor;
  intents.uxProgressBar.meterWarning.backgroundColor = intents.ux.feedbackWarning.feedbackColor;

  intents.uxProgressBar.meterSuccess.foregroundColor = intents.ux.feedbackSuccess.onFeedbackColor;
  intents.uxProgressBar.meterSuccess.backgroundColor = intents.ux.feedbackSuccess.feedbackColor;

  intents.uxProgressBar.meterInfo.foregroundColor = intents.ux.feedbackInfo.onFeedbackColor;
  intents.uxProgressBar.meterInfo.backgroundColor = intents.ux.feedbackInfo.feedbackColor;

  return intents;
}());
