import React, { useState, useCallback } from 'react';
import Button from '@ux/button';
import '@ux/button/styles';
import ProgressSteps from '@ux/progress-steps';
import '@ux/progress-steps/styles';

export default function DefaultExample() {
  const [currentValue, setCurrentValue] = useState(1);

  const _next = useCallback(() => {
    setCurrentValue(currentValue + 1);
  });

  const _back = useCallback(() => {
    setCurrentValue(currentValue - 1);
  });

  return (
    <div>
      <ProgressSteps steps={ ['Payment Details', 'Billing Information', 'Review', 'Checkout'] } value={ currentValue } />

      <div className='card-block'>
        <h4>Demo buttons</h4>

        <Button className='step-previous' onClick={ _back } disabled={ currentValue === 1 } text='Back' />
        <Button className='step-next' design='primary' onClick={ _next } disabled={ currentValue === 5 } text='Next' />
      </div>
    </div>
  );
}
