import Intents, { enums } from '@ux/intents';

// eslint-disable-next-line max-statements
export default (function () {
  const intents = new Intents();

  intents.create('@ux/progress-steps', {
    features: {
      [enums.features.ITEM]: {
        expressions: [
          enums.expressions.CHOSEN,
          enums.expressions.COMPLETED
        ],
        interactions: [
          enums.interactions.HOVERED
        ],
        styleProperties: [
          enums.properties.FONT_FAMILY,
          enums.properties.FONT_SIZE,
          enums.properties.FONT_WEIGHT,
          enums.properties.LINE_HEIGHT,
          enums.properties.FOREGROUND_COLOR,
          enums.properties.BACKGROUND_COLOR
        ]
      },
      [enums.features.LABEL]: {
        expressions: [
          enums.expressions.CHOSEN,
          enums.expressions.COMPLETED
        ],
        interactions: [
          enums.interactions.HOVERED
        ],
        styleProperties: [
          enums.properties.FONT_FAMILY,
          enums.properties.FONT_SIZE,
          enums.properties.FONT_WEIGHT,
          enums.properties.LINE_HEIGHT,
          enums.properties.FOREGROUND_COLOR
        ]
      }
    }
  });

  intents.uxProgressSteps.item.fontFamily = intents.ux.textLabel.fontFamily;
  intents.uxProgressSteps.item.fontSize = intents.ux.textLabel.fontSize;
  intents.uxProgressSteps.item.fontWeight = intents.ux.textLabel.fontWeight;
  intents.uxProgressSteps.item.lineHeight = intents.ux.textLabel.lineHeight;

  // Univisited steps
  intents.uxProgressSteps.item.foregroundColor = intents.ux.box.foregroundColor;
  intents.uxProgressSteps.item.backgroundColor = intents.ux.box.backgroundColor;

  // Hovered step
  intents.uxProgressSteps.itemHovered.backgroundColor = intents.ux.boxHighContrast.backgroundColor;
  intents.uxProgressSteps.itemHovered.foregroundColor = intents.ux.boxHighContrast.foregroundColor;

  // Current step
  intents.uxProgressSteps.itemChosen.backgroundColor = intents.ux.boxHighContrast.backgroundColor;
  intents.uxProgressSteps.itemChosen.foregroundColor = intents.ux.boxHighContrast.foregroundColor;

  // Completed (past) step
  intents.uxProgressSteps.itemCompleted.backgroundColor = intents.ux.feedbackSuccess.feedbackColor;
  intents.uxProgressSteps.itemCompleted.foregroundColor = intents.ux.feedbackSuccess.onFeedbackColor;

  // Labels beneath each step
  intents.uxProgressSteps.label.fontFamily = intents.ux.textLabel.fontFamily;
  intents.uxProgressSteps.label.fontSize = intents.ux.textLabel.fontSize;
  intents.uxProgressSteps.label.fontWeight = intents.ux.textLabel.fontWeight;
  intents.uxProgressSteps.label.lineHeight = intents.ux.textLabel.lineHeight;
  intents.uxProgressSteps.label.foregroundColor = intents.ux.box.foregroundColor;

  return intents;

}());
