import { RadioGroup } from '@ux/uxcore2';
import React from 'react';

export default class ToggleExample extends React.Component {
  constructor() {
    super();

    this.state = {
      selected: 'first'
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({
      selected: event.target.value
    });
  }

  render() {
    const onChange = this.handleChange;
    const inputs = [
      { label: 'First', name: 'stats', value: 'first', checked: (this.state.selected === 'first'), inline: true, onChange },
      { label: 'Second', name: 'stats', value: 'second', checked: (this.state.selected === 'second'), inline: true, onChange },
      { label: 'Third', name: 'stats', value: 'third', checked: (this.state.selected === 'third'), inline: true, onChange }
    ];

    return (
      <RadioGroup inputs={ inputs } design='toggle' proxy />
    );
  }
}
