import { RadioGroup } from '@ux/uxcore2';
import React from 'react';

export default class LabeledInlineExample extends React.Component {
  render() {
    var data = [
      { label: 'Read-only', name: 'permission', value: 'read', inline: true, defaultChecked: true },
      { label: 'Write', name: 'permission', value: 'write', inline: true },
      { label: 'Restricted', name: 'permission', value: 'restricted', inline: true }
    ];

    return (
      <RadioGroup inputs={ data } />
    );
  }
}
