import { RadioGroup, Form } from '@ux/uxcore2';
import React from 'react';

export default class PurchaseExample extends React.Component {
  constructor() {
    super();

    this.state = {
      selected: 'breakfast'
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({
      selected: event.target.value
    });
  }

  render() {
    const onChange = this.handleChange;
    const inputs = [
      { label: 'Breakfast', name: 'meal', value: 'breakfast', checked: (this.state.selected === 'breakfast'), inline: true, onChange },
      { label: 'Lunch', name: 'meal', value: 'lunch', checked: (this.state.selected === 'lunch'), inline: true, onChange },
      { label: 'Dinner', name: 'meal', value: 'dinner', checked: (this.state.selected === 'dinner'), inline: true, onChange }
    ];

    return (
      <Form>
        <RadioGroup inputs={ inputs } design='purchase' proxy />
      </Form>
    );
  }
}
