import Search from '../src';
import Button from '@ux/button';
import Star from '@ux/icon/star';
import React from 'react';

export default function IconButton(args) {
  const [value, setValue] = React.useState('Here is a default value');
  const button = <Button design='control' icon={ <Star/> } onClick={ () => console.log(value) }/>;

  return (
    <form role='search' action=''>
      <Search
        button={ button }
        value={ value }
        onChange={ setValue }
        { ...args }/>
    </form>
  );
}

IconButton.args = {
  id: 'my-search',
  placeholder: 'Enter a keyword here'
};
