import Search from '../src';
import Button from '@ux/button';
import React from 'react';

export default function Default(args) {
  const [value, setValue] = React.useState('Here is a default value');
  const button = <Button text='See result in console' design='primary' onClick={ () => console.log(value) }/>;

  return (
    <form role='search' action=''>
      <Search
        button={ button }
        value={ value }
        onChange={ setValue }
        { ...args }/>
    </form>
  );
}

Default.args = {
  id: 'my-search',
  placeholder: 'Enter a keyword here'
};
