import Search from '../src';
import Button from '@ux/button';
import React from 'react';

const results = [
  'cantaloupe',
  'carambola',
  'cherimoya',
  'cherries',
  'clementine',
  'coconut meat',
  'cranberries',
  'custard-apple'
];

export default function Results(args) {
  const [value, setValue] = React.useState('');
  const button = <Button text='See result in console' design='primary' onClick={ () => console.log(value) }/>;

  return (
    <form role='search' action=''>
      <Search
        button={ button }
        value={ value }
        onChange={ setValue }
        open={ Boolean(value) }
        items={ results.filter((result) => result.startsWith(value) && result !== value) }
        { ...args }/>
    </form>
  );
}

Results.args = {
  id: 'my-search',
  strategy: 'absolute',
  placeholder: 'Enter a keyword starting with "c" here'
};
