import Intents, { enums } from '@ux/intents';

export default (function () {
  const intents = new Intents();

  intents.create('@ux/spinner', {
    styleProperties: [
      enums.properties.FOREGROUND_COLOR,
      enums.properties.BACKGROUND_COLOR
    ]
  });

  /**
   * The color styles for the spinner, foreground color is the accent color
   */
  intents.uxSpinner.foregroundColor = intents.ux.feedback.feedbackColor;
  intents.uxSpinner.backgroundColor = intents.ux.boxLowContrast.backgroundColor;

  return intents;
}());
