import { css } from 'linaria';
import intents from './intents.config.js';

export default css`
:global() {
  code,
  kbd,
  pre,
  samp {
    font-family: "Courier", "Courier New", monospace;
  }

  code {
    font-size: calc(${intents.ux.text.fontSize} * 0.9); /* 90% */
    color: ${intents.ux.boxLowContrast.foregroundColor}; /* black */
    background-color: ${intents.ux.boxLowContrast.backgroundColor}; /* gray faint */
    border-radius: ${intents.ux.box.borderRadius};
    padding: .2em .4em;
  }

  kbd {
    font-size: calc(${intents.ux.text.fontSize} * 90%);
    color: ${intents.ux.boxLowContrast.foregroundColor}; /* black */
    background-color: ${intents.ux.boxLowContrast.backgroundColor}; /* gray faint */
    border-radius: calc(${intents.ux.box.borderRadius} - .125rem);
    padding: .2em .4em;
    box-shadow: inset 0 -.1rem 0 rgba(0, 0, 0, .25);

    kbd {
      font-weight: bolder;
      padding: 0;
      font-size: 100%;
      box-shadow: none;
    }
  }

  pre {
    font-size: calc(${intents.ux.text.fontSize} - 0.1rem);
    color: ${intents.ux.boxLowContrast.foregroundColor}; /* black */
    background-color: ${intents.ux.boxLowContrast.backgroundColor}; /* gray */
    display: block;
    margin-top: 0;
    margin-bottom: calc(${intents.ux.box.density} * 4);
    padding: calc(${intents.ux.box.density} * 2);

    code {
      padding: 0;
      font-size: inherit;
      color: inherit;
      background-color: transparent;
      border-radius: 0;
    }
  }

  .pre-scrollable {
    max-height: 21.25rem;
    overflow-y: auto;
  }
}`;
