import { css } from 'linaria';
import { functions, mixins } from '@ux/stylevars';

const {
  marketGroup
} = functions;

const {
  fontFace
} = mixins;

const brandFontFaces = {};

export default css`
:global() {
  ${marketGroup() === 'brand' ?
    Object.keys(brandFontFaces).map((brandFontFace) => `
      ${fontFace(...brandFontFace)};
    `).join('') : ''}
}
`;
