import { css } from 'linaria';
import intents from './intents.config.js';

const enablePrintStyles = true;

export default css`
:global() {
  /* stylelint-disable declaration-no-important, selector-no-qualifying-type, color-no-hex */

  /* Source: https://github.com/h5bp/html5-boilerplate/blob/master/src/css/main.css */

  /* ==========================================================================
  * Print styles.
  * Inlined to avoid the additional HTTP request:
  * http://www.phpied.com/delay-loading-your-print-css/
    ========================================================================== */

  ${enablePrintStyles ? `
    @media print {
      *,
      *::before,
      *::after {
        text-shadow: none !important;
        box-shadow: none !important;
      }

      a,
      a:visited {
        text-decoration: underline;
      }

      abbr[title]::after {
        content: " (" attr(title) ")";
      }

      pre {
        white-space: pre-wrap !important;
      }
      pre,
      blockquote {
        border: 1px solid ${intents.ux.box.borderColor};
        page-break-inside: avoid;
      }

      /* Printing Tables:
        http://css-discuss.incutio.com/wiki/Printing_Tables */

      thead {
        display: table-header-group;
      }

      tr,
      img {
        page-break-inside: avoid;
      }

      p,
      h2,
      h3 {
        orphans: 3;
        widows: 3;
      }

      h2,
      h3 {
        page-break-after: avoid;
      }

      /* Bootstrap specific changes start */

      /* Bootstrap components */
      .navbar {
        display: none;
      }
      .badge {
        border: 1px solid ${intents.ux.box.borderColor};
      }

      .table, .basegrid {
        border-collapse: collapse !important;

        td,
        th {
          background-color: ${intents.ux.box.backgroundColor} !important;
        }
      }
      .table-bordered, .basegrid {
        th,
        td {
          border: 1px solid ${intents.ux.box.borderColor} !important;
        }
      }

      /* Bootstrap specific changes end */
    }
  }` : '' }
}
`;
