import { css } from 'linaria';
import intents from './intents.config.js';

const svgPaletteColors = {
  'white': intents.ux.box.backgroundColor,
  'black': intents.ux.boxHighContrast.backgroundColor,
  'gray': intents.ux.boxLowContrast.backgroundColor,
  'product': intents.ux.boxLowContrast.backgroundColor,
  'primary': intents.ux.feedback.feedbackColor,
  'primary-o': intents.ux.feedback.feedbackColor,
  'success': intents.ux.feedbackSuccess.feedbackColor,
  'secondary': intents.ux.feedbackNeutral.feedbackColor,
  'secondary-o': intents.ux.feedbackNeutral.feedbackColor,
  'nav': intents.ux.feedbackWarning.feedbackColor,
  'info': intents.ux.feedbackInfo.feedbackColor,
  'warning': intents.ux.feedbackWarning.feedbackColor,
  'danger': intents.ux.feedbackCritical.feedbackColor,
  'feature': intents.ux.feedbackInternal.feedbackColor
};


export default css`
:global() {
/* SVG Fill and Stroke Colors */

  ${Object.entries(svgPaletteColors).map(([paletteColor, color]) => `
    .svg-fill-${paletteColor} {
      fill: ${color}
    }
    .svg-stroke-${paletteColor} {
      stroke: ${color}
    }
  `).join('')}

  .svg-fill-none {
    fill: none !important;
  }

  .svg-stroke-none {
    stroke-opacity: 0 !important;
  }
}
`;
