import { css } from 'linaria';
import intents from '../intents.config.js';

const bgPaletteColors = {
  'white': {
    bg: intents.ux.box.backgroundColor,
    fg: intents.ux.box.foregroundColor
  },
  'black': {
    bg: intents.ux.boxHighContrast.backgroundColor,
    fg: intents.ux.boxHighContrast.foregroundColor
  },
  'gray': {
    bg: intents.ux.boxLowContrast.backgroundColor,
    fg: intents.ux.boxLowContrast.foregroundColor
  },
  'product': {
    bg: intents.ux.boxLowContrast.backgroundColor,
    fg: intents.ux.boxLowContrast.foregroundColor
  },
  'primary': {
    bg: intents.ux.feedback.feedbackColor,
    fg: intents.ux.feedback.onFeedbackColor
  },
  'primary-o': {
    bg: intents.ux.feedback.feedbackColor,
    fg: intents.ux.feedback.onFeedbackColor
  },
  'success': {
    bg: intents.ux.feedbackSuccess.feedbackColor,
    fg: intents.ux.feedbackSuccess.onFeedbackColor
  },
  'secondary': {
    bg: intents.ux.feedbackNeutral.feedbackColor,
    fg: intents.ux.feedbackNeutral.onFeedbackColor
  },
  'secondary-o': {
    bg: intents.ux.feedbackNeutral.feedbackColor,
    fg: intents.ux.feedbackNeutral.onFeedbackColor
  },
  'nav': {
    bg: intents.ux.feedbackWarning.feedbackColor,
    fg: intents.ux.feedbackWarning.onFeedbackColor
  },
  'info': {
    bg: intents.ux.feedbackInfo.feedbackColor,
    fg: intents.ux.feedbackInfo.onFeedbackColor
  },
  'warning': {
    bg: intents.ux.feedbackWarning.feedbackColor,
    fg: intents.ux.feedbackWarning.onFeedbackColor
  },
  'danger': {
    bg: intents.ux.feedbackCritical.feedbackColor,
    fg: intents.ux.feedbackCritical.onFeedbackColor
  },
  'feature': {
    bg: intents.ux.feedbackInternal.feedbackColor,
    fg: intents.ux.feedbackInternal.onFeedbackColor
  }
};

export default css`
:global() {​
  .bg-link {
    &,
    &:focus,
    &:hover {
      color: inherit;
    }
  }

  ${Object.entries(bgPaletteColors).map(([paletteColor, colors]) => `
    .bg-${paletteColor},
    .bg-${paletteColor}-base,
    .bg-${paletteColor}-pastel,
    .bg-${paletteColor}-highlight,
    .bg-${paletteColor}-midrange,
    .bg-${paletteColor}-dark {
      background-color: ${colors.bg};
      color: ${colors.fg};
    }
    a.bg-${paletteColor},
    a.bg-${paletteColor}-base,
    a.bg-${paletteColor}-pastel,
    a.bg-${paletteColor}-highlight,
    a.bg-${paletteColor}-midrange,
    a.bg-${paletteColor}-dark {
      &:hover,
      &:focus {
        background-color: ${colors.bg};
        filter: brightness(90%);
      }
    }
  `).join('')}

  .bg-white {
    background-color: ${intents.ux.box.backgroundColor} !important; /* white */
  }
  ​
  .bg-transparent {
    background-color: transparent !important;
  }

  .bg-faded {
    background-color: ${intents.ux.boxLowContrast.backgroundColor}; /* gray faint */
  }

  .bg-brand-teal {
    background: ${intents.ux.feedback.feedbackColor}; /* 1bdbdb */
  }

  .bg-light {
    color: ${intents.ux.box.foregroundColor};
    background-color: ${intents.ux.box.backgroundColor};
  }

  .bg-faint {
    color: ${intents.ux.boxLowContrast.foregroundColor};
    background-color: ${intents.ux.boxLowContrast.backgroundColor};
  }

  .bg-midrange {
    color: ${intents.ux.boxLowContrast.foregroundColor};
    background-color: ${intents.ux.boxLowContrast.backgroundColor};
  }

  .bg-medium {
    color: ${intents.ux.boxLowContrast.foregroundColor};
    background-color: ${intents.ux.boxLowContrast.backgroundColor};
  }

  .bg-dark {
    color: ${intents.ux.boxHighContrast.foregroundColor};
    background-color: ${intents.ux.boxHighContrast.backgroundColor};
  }

  .flash-bg-primary {
    color: ${intents.ux.feedback.onFeedbackColor};
    background-color: ${intents.ux.feedback.feedbackColor};
  }

  .bg-pro-orange {
    color: ${intents.ux.feedbackWarning.onFeedbackColor};
    background-color: ${intents.ux.feedbackWarning.feedbackColor};
  }
}
`;
