import { css } from 'linaria';
import { gridBreakpoints, mediaBreakpointUp, breakpointInfix } from '../mixins/breakpoints';

const pullUtils = Object.keys(gridBreakpoints).map((breakpoint) => {
  const infix = breakpointInfix(breakpoint, gridBreakpoints);
  return `
  ${mediaBreakpointUp(breakpoint, `
    .float${infix}-left,
    .pull${infix}-left { float: left !important; }
    .float${infix}-right,
    .pull${infix}-right { float: right !important; }
    .float${infix}-none,
    .float${infix}-none { float: none !important; }
  `)}
`;
}).join('');

export default css`
:global() {
  ${pullUtils}
}
`;
