import { mix } from 'polished';

function themeColor(key = 'primary', themeColors) {
  return themeColors[key];
}

function themeColorLevel(name = 'primary', level = 0, themeColors, themeColorInterval = 0.08) {
  const color = themeColor(name, themeColors);
  const colorBase = level > 0 ? '#000' : '#fff';
  level = Math.abs(level);
  return mix(level * themeColorInterval, color, colorBase);
}

export {
  themeColor,
  themeColorLevel
};
