import colors from '../variables/_colors';

export default function palette(paletteName, tone = 'base', color = 'color') {
  if (paletteName === 'grey') {
    paletteName = 'gray';
  }

  if (colors[paletteName] === 'undefined') {
    throw new Error(`${paletteName} is not a palette color.`);
  }

  if (colors[paletteName][tone] === 'undefined') {
    throw new Error(`${paletteName} is not a palette tone.`);
  }

  return colors[paletteName][tone][color];
}
