/* eslint-disable max-params */

import { darken } from 'polished';
import { hoverFocus } from './hover';

export default function alertVariant(background, border, bodyColor, toggleBg = background, toggleIconColor = bodyColor) {
  return `
    background-color: ${background};
    border-color: ${border};
    color: ${bodyColor};
    hr {
      border-top-color: ${darken('0.05', border)};
    }
    .alert-link {
      body: ${bodyColor};
    }
    .alert-addon {
      border-color: ${border};
    }
    .close {
      ${hoverFocus(`color: ${bodyColor};`)}
    }

    .alert-toggle {
      background: ${toggleBg};
      color: ${toggleIconColor};
    }
  `;
}
