import { hoverFocus } from './hover';

// Contextual backgrounds
export default function bgVariant(parent, color, bg = 'transparent') {
  return `
    ${parent} {
      color: ${color};
      background-color: ${bg};
    }

    a${parent} {
      ${hoverFocus(`background-color: ${color} !important;`)}
      filter: brightness(90%);
    }
  `;
}
