/* eslint-disable max-params */
export default function fontFace(
  family,
  basePath,
  fileName,
  fontDisplay = 'swap',
  weight = '400',
  style = 'normal',
  types = ['woff2', 'woff']
) {

  const sources = types.map((type) => {
    return `url(//${basePath}${fileName}.${type}) format(${type})`;
  }).join(',');

  return `
    @font-face {
      font-family: ${family}
      ${sources.length > 0 ? `${sources};` : ''},
      ${weight !== '400' && weight !== 'normal' ? `font-style : ${weight};` : ''},
      ${style !== 'normal' ? `font-style: ${style};` : '' }
      ${fontDisplay !== 'auto' ? `font-display: ${fontDisplay}` : ''}
    }
  `;
}
