import fontFace from './font-face';
import { iconFontPath, iconFontName } from '../variables/_variables';

const fontDisplay = 'block';

function faceUxfont() {
  return `
    ${fontFace('uxfont', iconFontPath, iconFontName, fontDisplay)}
  `;
}

function faceUxfont2() {
  return `
    ${fontFace('uxfont-2', iconFontPath, iconFontName, fontDisplay)}  
  `;
}

export {
  faceUxfont,
  faceUxfont2
};
