import { enableHoverMediaQuery } from '../variables/_variables';

function hover(content) {
  return `
    &:hover {
      ${content}
    }
  `;
}

function hoverFocus(content) {
  if (enableHoverMediaQuery) {
    return `
      &:focus {
        ${content};
      }
      ${hover(content)}
    `;
  }
  return `
    &:focus,
    &:hover {
      ${content};
    }
  `;
}

function plainHoverFocus(content) {
  if (enableHoverMediaQuery) {
    return `
      &,
      &:focus {
        ${content}
      }
      ${hover(content)}
    `;
  }
  return `
    &,
    &:focus,
    &:hover {
      ${content}
    }
  `;
}

function hoverFocusActive(content) {
  if (enableHoverMediaQuery) {
    return `
      &:focus,
      &:active {
        ${content}
      }
      ${hover(content)}
    `;
  }
  return `
  &:focus,
  &:active,
  &:hover {
    ${content}
  }
  `;
}

export {
  hover,
  hoverFocus,
  hoverFocusActive,
  plainHoverFocus
};
