import { fontFamilyBase, fontWeightNormal, lineHeightBase } from '../variables/_variables';

export default function resetText() {
  return `
    font-family: ${fontFamilyBase};
    font-style: normal;
    font-weight: ${fontWeightNormal};
    line-height: ${lineHeightBase};
    text-align: left; 
    text-align: start; 
    text-decoration: none;
    text-shadow: none;
    text-transform: none;
    letter-spacing: normal;
    word-break: normal;
    word-spacing: normal;
    white-space: normal;
    line-break: auto;
  `;
}
