import { hover } from './hover';

export default function tableRowVariant(state, background) {
  const hoverBackground = background;
  const content = `
    background-color: ${hoverBackground};
    > td,
    > th {
      background-color: ${hoverBackground};
    }
  `;

  return `
  .table-${state} {
    &,
    > th,
    > td {
      background-color: ${background};
    }
  }

  .table-hover, .basegrid {
    .table-${state} {
      ${hover(content)}
    }
  }
  `;
}
