import palette from '../functions/palette';
import { borderRadiusContainers } from './border-radius';

export default function toggleSliderHandle(widthHeight, cursor = 'pointer') {
  return `
    ${borderRadiusContainers('50%')}
    width: ${widthHeight};
    height: ${widthHeight};
    cursor: ${cursor};
    background-color: ${palette('gray', 'light')};
    position: absolute;
    top: 5px;
    z-index: 4;
    display: inline-flex;
  `;
}
