import { uxIconSet1 } from '../variables/uxicons';

function uxicon(name, location = 'before', set = uxIconSet1) {
  if (set[name]) {
    const unicode = set[name];
    return `
      &:${location} {
        content: \\${unicode}
      }
    `;
  }
}

function rtlIcon() {
  return `transform: scaleX(1) '/*rtl:scaleX(-1)*/';`;
}

export {
  rtlIcon,
  uxicon
};
