import camelCase from 'camelcase';

/**
 * Convert variables from dash-case (scss-style) to camelCase (js-style)
 *
 * @param {object} variables - Override variables
 * @returns {object} vars
 */
export function keysToCamelCase(variables) {
  return Object.entries(variables || {}).reduce((acc, [key, value]) => {
    acc[camelCase(key)] = value;
    return acc;
  }, {});
}
