import Table from '../src';
import Button from '@ux/button';
import React from 'react';
import Checkmark from '@ux/icon/checkmark';
import Heartfill from '@ux/icon/heart';
import Trash from '@ux/icon/trash';

import '@ux/button/src/styles';

import '@ux/icon/checkmark/index.css';
import '@ux/icon/heart/index.css';
import '@ux/icon/trash/index.css';

export default function JsxExample() {
  function renderRows() {
    const icons = {
      checkmark: <Checkmark key='checkmark'/>,
      heartfill: <Heartfill key='heart'/>,
      trash: <Trash key='trash'/>
    };

    return Object.keys(icons).map(iconName => {
      const row = {};
      const iconElement = icons[iconName];
      row.icon = <td key='icon'>{ iconElement }</td>;
      row.button = <td key='btn'><Button aria-label={ iconName } design='secondary' size='small' icon={ iconElement }/></td>;
      row.text = <td key='text'>{ iconName }</td>;
      return row;
    });
  }

  return (
    <Table className='table'
      data={ renderRows() }>
      <thead>
        <tr>
          <th>Icon</th>
          <th>Button</th>
          <th>Text</th>
        </tr>
      </thead>
    </Table>
  );
}
