import Intents, { enums } from '@ux/intents';

// eslint-disable-next-line max-statements
export default (function () {
  const intents = new Intents();

  intents.create('@ux/table', {
    features: {
      // header <tr>
      [enums.features.LABEL]: {
        styleProperties: [
          enums.properties.FONT_FAMILY,
          enums.properties.FONT_SIZE,
          enums.properties.FONT_WEIGHT,
          enums.properties.LINE_HEIGHT,
          enums.properties.FOREGROUND_COLOR,
          enums.properties.BACKGROUND_COLOR,
          enums.properties.BORDER_COLOR
        ]
      },
      // body <tr>
      [enums.features.ITEM]: {
        styleProperties: [
          enums.properties.FONT_FAMILY,
          enums.properties.FONT_SIZE,
          enums.properties.FONT_WEIGHT,
          enums.properties.LINE_HEIGHT,
          enums.properties.FOREGROUND_COLOR,
          enums.properties.BACKGROUND_COLOR,
          enums.properties.BORDER_COLOR,
          'stripeColor'
        ]
      }
    }
  });

  // for header row
  intents.uxTable.label.borderColor = intents.ux.box.borderColor;
  intents.uxTable.label.fontFamily = intents.ux.textLabel.fontFamily;
  intents.uxTable.label.fontSize = intents.ux.textLabel.fontSize;
  intents.uxTable.label.fontWeight = intents.ux.textLabel.fontWeight;
  intents.uxTable.label.lineHeight = intents.ux.textLabel.lineHeight;
  intents.uxTable.label.foregroundColor = intents.ux.box.foregroundColor;
  intents.uxTable.label.backgroundColor = intents.ux.box.backgroundColor;

  // for body rows
  intents.uxTable.item.borderColor = intents.ux.box.borderColor;
  intents.uxTable.item.fontFamily = intents.ux.text.fontFamily;
  intents.uxTable.item.fontSize = intents.ux.text.fontSize;
  intents.uxTable.item.fontWeight = intents.ux.text.fontWeight;
  intents.uxTable.item.lineHeight = intents.ux.text.lineHeight;
  intents.uxTable.item.foregroundColor = intents.ux.box.foregroundColor;
  intents.uxTable.item.backgroundColor = intents.ux.box.backgroundColor;
  intents.uxTable.item.stripeColor = intents.ux.boxLowContrast.backgroundColor;

  return intents;
}());
