import React from 'react';
import Tabs from '../src';

import indices from './indices';

const { TabIndex, Tab, TabContent } = Tabs;

export default function DesignUnderline() {
  return (
    <Tabs>
      <TabIndex>
        { indices.map((index, i) => {
          return <Tab design='underline' id={ `index-${i}` } key={ i }>{ index }</Tab>;
        }) }
      </TabIndex>

      { indices.map((index, i) => {
        return <TabContent id={ `content-${i}` } key={ i }>Tab Content { i + 1 }.</TabContent>;
      }) }
    </Tabs>
  );
}
