import React from 'react';
import Tabs from '../src';

import indices from './indices';

const { TabIndex, Tab, TabContent } = Tabs;

const tabs = indices.map((index, i) => {
  return <Tab id={ `index-${i}` } key={ i }>{ index }</Tab>;
});

const content = indices.map((index, i) => {
  return <TabContent id={ `content-${i}` } key={ i }>Tab Content { i + 1 }.</TabContent>;
});

export default function StepSize() {
  return (
    <div>
      <h4>Slow</h4>
      <Tabs className='mb-3'>
        <TabIndex stepSize={ 50 }>
          { tabs }
        </TabIndex>

        { content }
      </Tabs>

      <h4>Fast</h4>
      <Tabs>
        <TabIndex stepSize={ 400 }>
          { tabs }
        </TabIndex>

        { content }
      </Tabs>
    </div>
  );
}
