import TextEntry from '../src';
import React, { useState } from 'react';

export default function Floating() {
  const [value, setValue] = useState('');

  return (
    <fieldset>
      <TextEntry
        type='text'
        label='Enter a name'
        id='floating'
        onChange={ setValue }
        value={ value }
      />
    </fieldset>
  );
}
