import TextEntry from '../src';
import React, { useState } from 'react';

export default function Invalid() {
  const [value, setValue] = useState('');

  const id = 'invalid-example';
  const invalid = /[^\w\d]/.test(value);

  return (
    <div className='sample-input'>
      <label htmlFor={ id }>Only letters and number please</label>
      <fieldset>
        <TextEntry
          type='text'
          id={ id }
          invalid={ invalid }
          onChange={ setValue }
          value={ value }/>
      </fieldset>
      { invalid && <span style={{ color: 'red' }}>You have got bad characters in there!</span> }
    </div>
  );
}
