import TextEntry from '../src';
import React, { useState } from 'react';

export default function Required() {
  const [value, setValue] = useState('');

  return (
    <fieldset>
      <TextEntry
        type='text'
        label='Enter a name'
        required
        id='required'
        onChange={ setValue }
        value={ value }/>
    </fieldset>
  );
}
