import TextEntry from '../src';
import React, { useState } from 'react';

export default function RTL() {
  const [value, setValue] = useState('');

  return (
    <div dir='rtl'>
      <fieldset>
        <TextEntry
          type='text'
          label='Enter a name'
          id='rtl'
          onChange={ setValue }
          value={ value }/>
      </fieldset>
    </div>
  );
}
