import TextEntry from '../src';
import React, { useState } from 'react';

export default function Suggestion() {
  const [value, setValue] = useState('');
  const [suggestion, setSuggestion] = useState('');

  return (
    <fieldset>
      <TextEntry
        type='text'
        value={ value }
        id='suggestion'
        onChange={ (val) => {
          setValue(val);
          /* Remember in RTL, suggestion text is prepended */
          setSuggestion(val ? val + ' and more!' : null);
        } }
        onKeyDown={ (ev) => {
          if (ev.key === 'Tab' && suggestion && value) {
            ev.preventDefault();
            setValue(suggestion);
            setSuggestion('');
          }
        } }
        suggestion={ suggestion }/>
    </fieldset>
  );
}
