import Intents, { enums } from '@ux/intents';

// eslint-disable-next-line max-statements
export default (function () {
  const intents = new Intents();

  intents.create('@ux/text-entry', {
    features: {
      placeholder: {
        styleProperties: [
          enums.properties.FOREGROUND_COLOR
        ]
      }
    }
  });

  // placeholder color
  intents.uxTextEntry.placeholder.foregroundColor = intents.ux.controlVoid.foregroundColor;

  return intents;

}());
