import React, { useState, useCallback } from 'react';
import Toggle from '@ux/toggle';
import '@ux/toggle/styles';

export default function Disabled() {
  const [checked, setChecked] = useState(false);
  const onChange = useCallback(() => {
    setChecked(!checked);
  });

  const toggleLabel = checked ? 'On' : 'Off disabled';

  return (
    <div>
      <Toggle testId='qa-toggle-disabled' checked={ checked } onChange={ onChange } label={ toggleLabel } disabled /><br />
      <Toggle checked={ true } onChange={ onChange } label='On Disabled' disabled />
    </div>
  );
}
