import React, { useState, useCallback } from 'react';
import Toggle from '@ux/toggle';
import '@ux/toggle/styles';

export default function InlineToggle() {
  const [state, setState] = useState({
    checked1: true,
    checked2: true,
    checked3: true
  });

  const onChange = useCallback((name) => {
    setState({ ...state, [name]: !state[name] });
  });

  const toggleLabel1 = state.checked1 ? 'super' : 'so';
  const toggleLabel2 = state.checked2 ? 'extra' : 'totally';
  const toggleLabel3 = state.checked3 ? 'very' : 'not';

  return (
    <p>
      <span>I am </span>
      <Toggle onChange={ () => onChange('checked1') } checked={ state.checked1 } label={ toggleLabel1 } />
      <Toggle onChange={ () => onChange('checked2') } checked={ state.checked2 } label={ toggleLabel2 } />
      <Toggle onChange={ () => onChange('checked3') } checked={ state.checked3 } label={ toggleLabel3 } />
      <span> excited!</span>
    </p>
  );
}
