import React, { useState, useCallback } from 'react';
import Toggle from '@ux/toggle';
import '@ux/toggle/styles';

export default function Small() {
  const [checked0, setChecked0] = useState(false);

  const onChange = useCallback(() => {
    setChecked0(!checked0);
  });

  const toggleLabel = checked0 ? 'On' : 'Off';
  return <Toggle id='smallToggle' checked={ checked0 } onChange={ () => onChange() } label={ toggleLabel } small />;
}
