import React from 'react';
import Tooltip from '../src';

export default function HideClose(args) {
  return (
    <div>
      <p>
        This tooltip has no close button
        <Tooltip
          id='hideclose-hover-tooltip'
          message='Because openOnHover is `true`'
          openOnHover={ true }
          autoHideTimeout={ 600 }
          { ...args }>
        </Tooltip>.
      </p>
      <p>
        This tooltip has still has a close button
        <Tooltip
          id='hideclose-click-tooltip'
          message='Because openOnHover is `false`'
          { ...args }>
        </Tooltip>.
      </p>
    </div>
  );
}

HideClose.args = {
  hideClose: true
};
