import React from 'react';
import Tooltip from '../src';

export default function HoverAutohide(args) {
  return (
    <div>
      This tooltip will be
      <Tooltip { ...args }> shown on hover and automatically hidden</Tooltip>.
    </div>
  );
}

HoverAutohide.args = {
  id: 'hover-autohide-tooltip',
  message: 'This Tooltip is shown on hover and automatically hidden in two seconds',
  openOnHover: true,
  autoHideTimeout: 2000
};
