import React from 'react';
import Tooltip from '../src';

export default function Multiple(args) {
  return (
    <div>
      Only <Tooltip id='first-tooltip' message='First' { ...args }>one</Tooltip> should be open at
      the same time, <Tooltip id='second-tooltip' message='Second' { ...args }>two</Tooltip> are not allowed.
    </div>
  );
}
