import React from 'react';
import Tooltip from '../src';
import lipsum from 'bob-ross-lipsum';

export default function OverText(args) {
  return (
    <div>
      Click<Tooltip message={ lipsum(3) } { ...args }></Tooltip>
        to render a tooltip.
    </div>
  );
}

OverText.args = {
  id: 'over-text',
  text: 'some text '
};
