import React, { useState } from 'react';
import Tooltip from '../src';
import Button from '@ux/button';
import lipsum from 'bob-ross-lipsum';

export default function ControlledTooltip(args) {
  const [open, setOpen] = useState(false);

  return (
    <div>
      <div>
        <Button onClick={ () => setOpen(!open) } design='primary'>
          Toggle Tooltip
        </Button>
      </div>
      <div>
        quis dolore dolore dolore
        {' '}
        <Tooltip
          open={ open }
          message={ lipsum(3) }
          { ...args }>
        </Tooltip>
        {' '}
        incididunt in consectetur veniam adipisicing
      </div>
    </div>
  );
}

ControlledTooltip.args = {
  id: 'basic-tooltip',
  title: 'My first tooltip',
  persistent: true
};
